pragma solidity ^0.8.27;

import {MerkleProof} from "@openzeppelin/contracts/utils/cryptography/MerkleProof.sol";
import {SafeERC20, IERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {ERC20} from "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import {IGauge} from "./interfaces/IGauge.sol";
import {IGaugeController} from "./interfaces/IGaugeController.sol";
import {SYKPuller} from "./SYKPuller.sol";
import {IXStrykeToken} from "./interfaces/IXStrykeToken.sol";

/**
 * @notice Contract for distributing token rewards to Orange vault
 * depositors. The contract is able to distribute token rewards
 * distributed by stryke via their gauges as well as any ERC20 token
 * transferred to the OrangeDistributor contract
 *
 * @dev In order to distribute token rewards for an orange vault, the
 * reward amounts need to be calculated for the vault depositors off-
 * chain. The total reward amount should be sent to the distributor
 * contract, either by calling pullNext for syk rewards or transferring
 * the tokens directly to the distributor. A merkle root then needs to be 
 * calculated based on the amount of tokens that are to be distributed to
 * each vault depositor (this is performed off chain) and sent to the 
 * distributor contract via updateMerkleRoot
 */
contract OrangeDistributor is SYKPuller {
    using SafeERC20 for IERC20;

    event MerkleRootUpdated(address indexed _vault, address indexed _token, bytes32 _newMerkleRoot);
    event RewardClaimed(address indexed _user, address indexed _vault, address indexed token, uint _amount);

    error InvalidProof();

    // Mapping from vault to token to merkle root
    mapping (address => mapping (address => bytes32)) public merkleRoot;

    // Mapping from vault to depositor to token to amount of token claimed
    mapping (address => mapping (address => mapping (address => uint))) public claimed;

    function initialize(IGaugeController _controller, address _keeper, address[] memory _vaults, address[] memory _gauges) external initializer {
        __SYKPuller_init(_controller, _keeper, _vaults, _gauges);
    }

    /**
     * @notice Claim all the token rewards for a vault
     * @param _vault The vault for which to claim rewards
     * @param _token The token to claim
     * @param _amount Amount of token to claim as reward
     * @param merkleProof Merkle proof
     */
    function claim(address _vault, address _token, uint256 _amount, bytes32[] calldata merkleProof) public {
        claimed[_vault][_token][msg.sender]+=_amount;
        
        bytes32 leaf = keccak256(abi.encodePacked(msg.sender, claimed[_vault][_token][msg.sender]));
        require(MerkleProof.verifyCalldata(merkleProof, merkleRoot[_vault][_token], leaf), InvalidProof());

        if (_token==syk) {
            IERC20(syk).safeIncreaseAllowance(xSyk, _amount/2);
            IXStrykeToken(xSyk).convert(_amount / 2, msg.sender);
            IERC20(_token).safeTransfer(msg.sender, _amount - _amount/2);
        } else {
            IERC20(_token).safeTransfer(msg.sender, _amount);
        }

        emit RewardClaimed(msg.sender, _vault, _token, _amount);
    }

    /**
     * @notice Claim multiple token rewards in a single call
     */
    function batchClaim(address[] calldata _vaults, address[] calldata _tokens, uint256[] calldata _amounts, bytes32[][] calldata merkleProofs) external {
        for (uint i = 0; i<_vaults.length; i++) {
            claim(_vaults[i], _tokens[i], _amounts[i], merkleProofs[i]);
        }
    }

    /**
     * @notice Update the merkle root for the reward token distribution of a vault
     * @param _vault The vault whose token is to be distributed using the merkle root
     * @param _token The reward token for the vault
     * @param _merkleRoot New root for reward distribution
     */
    function updateMerkleRoot(address _vault, address _token, bytes32 _merkleRoot) external restricted {
        merkleRoot[_vault][_token] = _merkleRoot;
        emit MerkleRootUpdated(_vault, _token, _merkleRoot);
    }

    /**
     * @notice Withdraw funds in case of emergency
     */
    function emergencyWithdrawal(address _token, uint _amount) external onlyOwner {
        IERC20(_token).safeTransfer(owner(), _amount);
    }
}
","keccak256":"0x4040bc9fe466c06b80915d0bb96c4b296fe9e053b28211ce61900b2ba907cc8d","license":"UNLICENSED"},"contracts/SYKPuller.sol":{"content":"// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.27;

import {ERC20} from "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import {IGauge} from "./interfaces/IGauge.sol";
import {IGaugeController} from "./interfaces/IGaugeController.sol";
import {OwnableUpgradeable} from "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import {Initializable} from "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";

/**
 * @notice Abstract contract with logic for pulling rewards
 * from stryke reward gauges
 *
 * @dev The SYKPuller contract needs to be made the owner of the gauges
 * that it will be pulling rewards from
 */
abstract contract SYKPuller is OwnableUpgradeable {

    event SetGauge(address indexed _vault, address indexed _gauge);
    event RewardPulled(address indexed _vault, uint _amount);
    event SkipPulls(address indexed _vault, uint _nextEpochToPull);
    event SetKeeper(address indexed _keeper);

    error Unauthorized();
    error VaultGaugeArrayMismatch();

    // Mapping from vault to next reward epoch to pull
    mapping (address => uint) public nextStrykeEpochToPull;

    // Mapping from vault to epoch to tokens rewarded
    mapping (address => mapping (uint => uint)) public epochRewards;

    // Mapping from vault to gauge
    mapping (address => address) public gauges;

    // Stryke gauge controller
    IGaugeController public controller;

    address public keeper;

    // Stryke tokens
    address public syk;
    address public xSyk;

    function __SYKPuller_init (IGaugeController _controller, address _keeper, address[] memory _vaults, address[] memory _gauges) internal initializer {
        __Ownable_init(msg.sender);
        controller = _controller;
        syk = controller.syk();
        xSyk = controller.xSyk();
        keeper = _keeper;

        require(_vaults.length==_gauges.length, VaultGaugeArrayMismatch());
        for (uint i = 0; i<_vaults.length; i++) {
            gauges[_vaults[i]] = _gauges[i];
            emit SetGauge(_vaults[i], _gauges[i]);
        }

        emit SetKeeper(_keeper);
    }

    modifier restricted() {
        require(msg.sender==keeper || msg.sender==owner(), Unauthorized());
        _;
    }

    /**
     * @notice Check if the rewards for the next epoch can be pulled
     */
    function canPullNext(address _vault) external view returns (bool) {
        return controller.epochFinalized(nextStrykeEpochToPull[_vault]);
    }

    /**
     * @notice Set the gauge for an orange vault
     * @dev SYKPuller needs to be made the owner of the gauge, to allow
     * pulling reward
     */
    function setGauge(address _vault, address _gauge) external onlyOwner {
        gauges[_vault] = _gauge;
        emit SetGauge(_vault, _gauge);
    }

    /**
     * @notice Update keeper address
     */
    function setKeeper(address _keeper) external onlyOwner {
        keeper = _keeper;
        emit SetKeeper(_keeper);
    }

    /**
     * @notice Admin function to manually set the next epoch whose rewards
     * will be pulled.
     */
    function skipPulls(address _vault, uint _nextEpochToPull) external onlyOwner {
        nextStrykeEpochToPull[_vault] = _nextEpochToPull;
        emit SkipPulls(_vault, _nextEpochToPull);
    }

    /**
     * @notice Pull the rewards for the next epoch from the gauge
     */
    function pullNext(address _vault) external restricted {
        uint nextEpoch = nextStrykeEpochToPull[_vault]++;
        uint balanceBefore = ERC20(syk).balanceOf(address(this));
        IGauge(gauges[_vault]).pull(nextEpoch);
        uint balanceAfter = ERC20(syk).balanceOf(address(this));
        epochRewards[_vault][nextEpoch] = balanceAfter - balanceBefore;
        emit RewardPulled(_vault, balanceAfter - balanceBefore);
    }
}
